class DossierMedicalRepository extends \Doctrine\ORM\EntityRepository {

    public function getPatient($data) {
        $query = $this->createQueryBuilder('p')
           ->leftJoin('p.etudiant', 'e')
            ->addSelect('e')
            ->where("e.matricule LIKE :cher")
            ->setParameter('cher', '%'.$data.'%')
            ->getQuery();
        return $query->getResult();
    }

    public function getStatistiqueHomme() {
  
        return $this->createQueryBuilder('g')
                ->leftJoin('g.etudiant', 'e')
                        ->addselect('e')
                        ->where('e.genre = :a')
                        ->setParameter('a','Masculin')
                        ->getQuery()
                        ->getResult();
    }
